mapper.select:
SELECT member_idx, id, password, nickname, auth_type, token, member_created_date, member_modified_date
FROM member
WHERE id=#{id} AND password=#{password}

SELECT member_idx, id, password, nickname, auth_type, token, member_created_date, member_modified_date
FROM member
WHERE id = #{id}

SELECT member_idx, id, password, nickname, auth_type, token, member_created_date, member_modified_date
FROM member
WHERE member_idx = #{memberIdx}
